import React, { Component } from 'react';
import CurrentVisitorCount from '../../components/CurrentVisitorCount';
import AddVisitor from '../../components/AddVisitor';
import Request from '../../helpers/request'

class VisitorCount extends Component {
  constructor(props){
    super(props);
    this.state = {visitor_count: []}
  }

  componentDidMount(){
    // const request = new Request();
    // request.get("/park").then((data) => {
    //   console.log(data);
    //   this.setState({visitor_count: data})
    // })

    fetch("/park")
    .then((res) => {
      res.json()})
      .then((data) => {
        console.log(data);
      })
  }

  render() {
    if(!this.state.visitor_count ){
      return(<p>loading</p>);
    }
    return (
        <>
          <div id = "visitor_count">
            <p>VisitorCount</p>
            <CurrentVisitorCount count = {this.state.visitor_count}/>
          </div>
        </>
    );
  }
}

export default VisitorCount;
