import React, { Component } from 'react';
import CurrentVisitorCount from '../../components/CurrentVisitorCount';
import AddVisitor from '../../components/AddVisitor';
import Request from '../../helpers/request'

class VisitorCount extends Component {
  constructor(props){
    super(props);
    this.state = {park_open: [], rampaging: []}
    this.changeParkOpen = this.changeParkOpen.bind(this);
  }

  componentDidMount(){
    const request = new Request();
    request.get("/park").then((data) => {
      console.log(data[0]);
      this.setState({park_open: true});
      this.setState({rampaging: false});
    })
  }

  changeParkOpen() {
    if(this.state.park_open){
      this.setState({park_open: false});
    }
    else{
      this.setState({park_open: true});
    }
  }

  changeRampageStatus(){
    if(this.state.rampaging){
      this.setState({rampaging: false});
    }
    else{
      this.setState({rampaging: true});
    }
  }

  render() {
    return (
        <>
          <div id = "park_status">
            <p>Current Park Status</p>
            <p>Park Open</p>
            <button onClick={this.changeParkOpen}>{this.state.park_open}</button>
            <p>Rampage Status</p>
            <button onClick={this.changeRampageStatus}>{this.state.rampage}</button>
          </div>
        </>
    );
  }
}

export default VisitorCount;
