import React, { Component } from 'react';
import Request from '../../helpers/request';
import PaddockBoxStatus from '../../components/PaddockBoxStatus';

class VisitorCount extends Component {
  constructor(props){
    super(props);
    this.state = {
      paddocks: []
    }
  }

  componentDidMount(){
    const request = new Request();
    request.get("/paddocks/all").then((data) => {
        // console.log(data);
        let data_array =[];
        for(let i = 0; i < data.lenght; i++){
          data_array.push(data[i]);
        }
        this.setState({paddocks: data_array})
    })
  }

  render() {
      return(
        <>
            <PaddockBoxStatus paddocks = {this.state.paddocks[0]}/>
        </>
    );
  }
}

export default VisitorCount;
