import React, { Component } from 'react';
import CurrentVisitorCount from '../../components/CurrentVisitorCount';
import AddVisitor from '../../components/AddVisitor';
import Request from '../../helpers/request'

class VisitorCount extends Component {
  constructor(props){
    super(props);
    this.state = {visitor_count: null}
  }

  componentDidMount(){
    const request = new Request();
    request.get("/").then((visitors) => {
      this.setState({visitor_count: visitors})
    })
  }

  render() {
    return (
        <>
          <div id = "visitor_count">
            <p>VisitorCount</p>
            <CurrentVisitorCount count = {this.state.visitor_count}/>
          </div>
        </>
    );
  }
}

export default VisitorCount;
