import React, { Component } from 'react';
import Request from '../../helpers/request';

class RemoveDino extends Component {
  constructor(props){
    super(props)
    this.state = {
      dinosaurs: [],
      dinoSelected: [],
      id: []
    }
    this.handleDinoSelected = this.handleDinoSelected.bind(this);
  }

componentDidMount(){
  let dinos = [];
  const request = new Request();
  request.get("/dinosaurs/").then((data) => {
    this.setState({dinosaurs: data});
    })
}

handleDinoSelected(dinoId){
  this.state.dinoSelected = dinoId;
}

render(){
  let dinos = this.state.dinosaurs;
  let optionItems = dinos.map((dinosaur) =>
               <option key={dinosaur.id}>{dinosaur.name}</option>
           );
  console.log(this.state.dinoSelected);

       return (
        <div>
            <>
            <p>Select Dinosaur By Name</p>
            <select onChange={this.handleDinoSelected(dinosaur.id) }>
               {optionItems}
            </select>
            <input type="submit" value="Kill Dinosaur" onSubmit={() => this.handleDinosaurRemove(this.state.dinoSelected)}/>
            </>
        </div>
       )
}
}

export default RemoveDino;
