import React, { Component } from 'react';
import Request from '../../helpers/request';

class RemoveDino extends Component {
  constructor(props){
    super(props)
    this.state = {
      dinosaurs: [],
      id: []
    }
  }

componentDidMount(){
  let dinos = [];
  const request = new Request();
  request.get("/dinosaurs/").then((data) => {
    this.setState({dinosaurs: data});
    })
}

render(){
  let dinos = this.state.dinosaurs;
  let optionItems = dinos.map((dinosaur) =>
               <option key={dinosaur.id}>{dinosaur.name}</option>
           );

       return (
        <div>
            <>
                <p>Select Dinosaur</p>
                <section>
                  {optionItems}
               </section>
            </>
        </div>
       )









  return(
    <>
      <h4>RemoveDino</h4>
    </>
  );
}
}

export default RemoveDino;
