import React, { Component } from 'react';
import PaddockStatus from './park_view/PaddockStatus';
import ParkStatus from '../components/park_view/ParkStatus';
import Request from '../helpers/request';
import VisitorCount from '../components/park_view/VisitorCount';

class ParkView extends Component {
  constructor(props){
    super(props)
    this.state = {parkOpen: [], rampaging: [], visitorCount: [], capacity: 15000}
  }


  render() {
    return (
        <>
          <h2>ParkView</h2>
          <div id="park_info">
          <VisitorCount onClick={this.handleVisitorAdd} capacity = {this.state.capacity}/>
          <ParkStatus onClick = {this.handleStatusChange} parkOpen = {this.state.parkOpen} rampaging = {this.state.rampaging} />
          </div>
          <PaddockStatus />
        </>
    );
  }
}

export default ParkView;
