  // }

  // <CurrentVisitorCount count = {this.state.visitor_count} capacity = {this.state.capacity}/>
  // <AddVisitor handleAddVisitor = {this.handleAddVisitor}/>
  render() {
    return (
        <>
          <div id = "park_status">
            <p>Current Park Status</p>
          </div>
        </>
    );
  }
}

export default VisitorCount;
